BEGIN
-- Create a temp table to store the select results CREATE TABLE #PageIndex ( [IndexId] int IDENTITY (1, 1) NOT NULL, [LEADPK] bigint )
-- Insert into the temp table INSERT INTO #PageIndex ( [LEADPK] ) SELECT TOP {3} [LEADPK] FROM [dbo].[ZohoLeads] {0} ORDER BY {1}
-- Return paged results SELECT O.[Activities Involved], O.[Calls Involved], O.[Company], O.[CONVERTED], O.[Created By], O.[Created Time], O.[Email], O.[Email Opt Out], O.[Events Involved], O.[Features of Interest], O.[First Name], O.[First Time Caller (new lead)], O.[If No Longer Interested], O.[Industry], O.[Last Activity Time], O.[Last Name], O.[Last Visited Time], O.[Lead Owner Id], O.[Lead Owner], O.[Lead Source], O.[Lead Status], O.[LEADID], O.[Mobile], O.[Modified By], O.[Modified Time], O.[Phone], O.[Rating], O.[Secondary Email], O.[State], O.[Submission Time], O.[Submitted On], O.[Tasks Involved], O.[Time Zone], O.[URL], O.[Website], O.[Worries], O.[LEADPK], O.[WDay8-11], O.[WDay11-2], O.[WDay2-5], O.[WDay5-8], O.[Sat8-11], O.[Sat11-2], O.[Sat2-5], O.[Sat5-8], O.[Sun8-11], O.[Sun11-2], O.[Sun2-5], O.[Sun5-8] FROM [dbo].[ZohoLeads] O, #PageIndex PageIndex WHERE PageIndex.IndexId > {2} AND O.[LEADPK] = PageIndex.[LEADPK] ORDER BY PageIndex.IndexId
-- get total count SELECT COUNT(*) AS TotalRowCount FROM [dbo].[ZohoLeads] {0};
DROP TABLE #PageIndex END

